export class Pokemon
{
    /**
     * Atributos de la clase Pokemon
     */
    private nombre: string;
    private peso: number;
    private altura: number;
    private tipo: string;
    private estadisticasBasicas = Array(4);

    /**
     * Constructor de la clase pokemon
     * @param nombre nombre del pokemon
     * @param peso peso del pokemon
     * @param altura altura del pokemon
     * @param tipo tipo del pokemon
     * @param estadisticasBasicas estadisticas basicas del pokemon [ataque, defensa, velocidad, vida]
     */
    constructor(nombre :string, peso :number, altura: number, tipo: string, estadisticasBasicas: number[]) 
    {
        this.nombre = nombre;
        this.peso = peso;
        this.altura = altura;
        this.tipo = tipo;
        this.estadisticasBasicas = estadisticasBasicas;
    }

    /**
     * Getter del nombre del pokemon
     * @returns retorna el nombre del pokemon
     */
    public getNombre()
    {
        return this.nombre;
    }

    /**
     * Getter del peso del pokemon
     * @returns retorna el peso del pokemon
     */
    public getPeso()
    {
        return this.peso;
    }

    /**
     * Getter de la altura del pokemon
     * @returns retorna la altura del pokemon
     */
    public getAltura()
    {
        return this.altura;
    }

    /**
     * Getter del tipo del pokemon
     * @returns retorna el tipo del pokemon
     */
    public getTipo()
    {
        return this.tipo;
    }

    /**
     * Getter del ataque del pokemon
     * @returns retorna el ataque del pokemon
     */
    public getAtaque()
    {
        return this.estadisticasBasicas[0];
    }

    /**
     * Getter de la defensa del pokemon
     * @returns retorna la defensa del pokemon
     */
    public getDefensa()
    {
        return this.estadisticasBasicas[1];
    }

    /**
     * Getter de la velocidad del pokemon
     * @returns retorna la velocidad del pokemon
     */
    public getVelocidad()
    {
        return this.estadisticasBasicas[2];
    }

    /**
     * Getter de la vida
     * @returns retorna la vida del pokemon
     */
    public getVida()
    {
        return this.estadisticasBasicas[3];
    }

    /**
     * Setter de vida del pokemon
     * @param vida cambia el valor del atributo vida del pokemon
     */
    public setVida(vida: number)
    {
        this.estadisticasBasicas[3] = vida;
    }
}

import { Pokemon } from "./pokemon";

export class Combat
{
    private pokemon1: Pokemon;
    private pokemon2: Pokemon;

    /**
     * Constructor de la clase Combat
     * @param pokemon1 representa un pokemon 
     * @param pokemon2 representa un pokemon 
     */
    constructor(pokemon1: Pokemon, pokemon2: Pokemon) 
    {
        this.pokemon1 = pokemon1;
        this.pokemon2 = pokemon2;
    };

    /**
     * Calcula el daño realizado entre dos pokemons
     * @param tipoPokemonA selecciona el tipo del pokemon
     * @param tipoPokemonB selecciona el tipo del pokemon
     * @param capacidadAtaqueA selecciona daño de ataque del pokemon
     * @param capacidadAtaqueB selecciona daño de ataque del pokemon
     * @returns devuelve el calculo realizado
     */
    calculoDanios(tipoPokemonA: string, tipoPokemonB: string, capacidadAtaqueA: number, capacidadAtaqueB: number)
    {
        let danio = 0;
        let efectividad = 0;

        switch (tipoPokemonA)
        {
            case `Fuego`:
                if (tipoPokemonB = `Planta`)
                {
                    efectividad = 2;
                } else if (tipoPokemonB = `Agua`) 
                {
                    efectividad = 0.5;
                } else efectividad = 1;

                break;

            case `Agua`:
                if (tipoPokemonB = `Planta`)
                {
                    efectividad = 0.5;
                } else if (tipoPokemonB = `Fuego`) 
                {
                    efectividad = 2;
                } else efectividad = 2;

                break;

            case `Electrico`:
                if (tipoPokemonB = `Agua`)
                {
                    efectividad = 2;
                } else if (tipoPokemonB = `Fuego`) 
                {
                    efectividad = 1;
                } else efectividad = 1;

                break;

            case `Planta`:
            if (tipoPokemonB = `Agua`)
            {
                efectividad = 2;
            } else if (tipoPokemonB = `Agua`) 
            {
                efectividad = 0.5;
            } else efectividad = 1;

            break;

        }

        danio = 50 * (capacidadAtaqueA / capacidadAtaqueB) * efectividad;
        return danio;
    }

    /**
     * Simula un combate Pokémon
     * @param pokemonA Pokemon para realizar un combate
     * @param pokemonB Pokemon para realizar un combate
     * @returns retorna el Pokemon ganador
     */
    start()
    {
        /** Muestra un mensaje por pantalla de comienzo del combate entre dos pokemons */
        console.log(`El combate entre ${this.pokemon1} vs ${this.pokemon2} está apunto de empezar:`);

        /** Contador de turnos */
        let i: number  = 0;

        /** Pokemons auxiliares para determinar qué Pokémon empieza a atacar */
        let pokemonPrimeroAtacar: Pokemon;
        let pokemonSegundoAtacar: Pokemon;

        /** Pokemon que será el ganador del combate */
        let pokemonGanador: Pokemon;

        /** Variable para el calculo de daños */
        let danio: number = 0;

        /** Determina quien empieza en atacar según la velocidad del Pokémon */
        console.log(`Vamos a conocer quien es el primer Pokémon en empezar en base a su velocidad:`);
        if(this.pokemon1.getVelocidad() > this.pokemon2.getVelocidad())
        {
            pokemonPrimeroAtacar = this.pokemon1;
            pokemonSegundoAtacar = this.pokemon2;
        } else {
            pokemonPrimeroAtacar = this.pokemon2;
            pokemonSegundoAtacar = this.pokemon1;
        }

        /** Bucle tipo while que compara las vidas de ambos pokemons, y va realizando los ataques y defensas de ambos a través de la funcion calculoDanios() */
        while(pokemonPrimeroAtacar.getVida() > 0 || pokemonSegundoAtacar.getVida() > 0)
        {
            console.log(`Turno ${i+1}: los tipos son ${pokemonPrimeroAtacar.getTipo()} vs ${pokemonSegundoAtacar.getTipo()}`);
            console.log(`El Pokémon ${pokemonPrimeroAtacar.getNombre()} realiza un ataque con poder ${pokemonPrimeroAtacar.getAtaque()}`);
            console.log(`El Pokémon ${pokemonSegundoAtacar.getNombre()} defiende con un poder de ${pokemonSegundoAtacar.getDefensa()}`);
            danio = this.calculoDanios(pokemonPrimeroAtacar.getTipo(), pokemonSegundoAtacar.getTipo(), pokemonPrimeroAtacar.getAtaque(), pokemonSegundoAtacar.getDefensa());
            console.log(`El daño realizado es de ${danio}`);
            pokemonSegundoAtacar.setVida(pokemonSegundoAtacar.getVida()-danio);

            console.log(`El Pokémon ${pokemonSegundoAtacar.getNombre()} realiza un ataque con poder ${pokemonSegundoAtacar.getAtaque()}`);
            console.log(`El Pokémon ${pokemonPrimeroAtacar.getNombre()} defiende con un poder de ${pokemonPrimeroAtacar.getDefensa()}`);
            danio = this.calculoDanios(pokemonSegundoAtacar.getTipo(), pokemonPrimeroAtacar.getTipo(), pokemonSegundoAtacar.getAtaque(), pokemonPrimeroAtacar.getDefensa());
            console.log(`El daño realizado es de ${danio}`);
            pokemonPrimeroAtacar.setVida(pokemonPrimeroAtacar.getVida()-danio);

            i++;            
        }

        /** Determina quien es el pokemon ganador */
        if(pokemonPrimeroAtacar.getVida() > pokemonSegundoAtacar.getVida())
        {
            pokemonGanador = pokemonPrimeroAtacar;
        } else {
            pokemonGanador = pokemonSegundoAtacar;
        }

        return pokemonGanador;

    }
}

import { Pokemon } from "./pokemon";

export class Pokedex
{
    /**
     * Atributos de la clase Pokedex
     *      Arrays de Pokemons
     */
    private nPokemons: Pokemon[] = [];

    /**
     * Constructor de la Pokedex
     * @param nPokemons arrays con los pokemons
     */
    constructor(nPokemons: Pokemon[]) 
    {
        this.nPokemons = nPokemons;
    };

    /**
     * Getter de la pokedex para una posición
     * @param position posición de la pokedex a comprobar
     * @returns retorna el pokemon en esa posición
     */
    public getPokemonPosition(position: number)
    {
        return this.nPokemons[position];
    }

    /**
     * Añade un pokemon nuevo en la pokedex
     * @param nuevoPokemon pokemon a añadir a la pokedex
     */
    public addPokemon(nuevoPokemon: Pokemon)
    {
        this.nPokemons.push(nuevoPokemon);
    }

    /**
     * Tamaño de la pokedex
     * @returns Retorna el tamaño de la pokedex [numero de pokemons dentro]
     */
    public tamanioPokedex()
    {
        return this.nPokemons.length;
    }

    /**
     * Busca un pokemon en la pokedex
     * @param buscarPokemon busca un pokemon en concreto a través de un booleano
     * @returns true si lo encuentra y false si no lo encuentra
     */
    public searchPokemon(buscarPokemon: Pokemon)
    {
        let encontrado :Boolean = false;

        for(let i = 0; i < this.nPokemons.length; i++)
        {
            if(buscarPokemon == this.nPokemons[i])
            {
                encontrado = true;
            }
        }
        return encontrado;
    }

    /**
     * Elimina un pokemon de la pokedex
     * @param eliminarPokemon elimina un pokemon de la pokedex utilizando el metodo searchPokemon()
     */
    public deletePokemon(eliminarPokemon: Pokemon)
    {
        if(this.searchPokemon(eliminarPokemon))
        {
            for(let i = 0; i < this.nPokemons.length; i++)
            {
                if(eliminarPokemon == this.nPokemons[i])
                {
                    this.nPokemons.splice(i,1);
                }
            }
        }
    }
}

/**
 * Clase Estudiante 
 * @class Estudiante
 * @param correoInst correo institucional
 * @method getCorreo getter de correo institucional
 * @method setCorreo setter de correo institucional
 */
import {Persona} from './persona';

export class Estudiante extends Persona 
{
    private correoInst: string;

    constructor(nombre: string, apellidos: string, fechaNacimiento: string,
    genero: string, correoInst: string) {
        super(nombre, apellidos, fechaNacimiento, genero);
        this.correoInst = correoInst;
    };

    public getCorreo()
    {
        return this.correoInst;
    }

    public setCorreo(correoNuevo: string)
    {
        this.correoInst = correoNuevo;
    }
}

/**
 * Clase que representa una persona
 * @class Persona 
 * @param nombre atributo nombre de la clase
 * @param apellidos atributo apellidos de la clase
 * @param fechaNacimiento atributo fecha de nacimiento de la clase
 * @param genero atributo genero de la clase
 * @method getNombre getter del nombre
 * @method getApellidos getter de apellidos
 * @method getFechaNacimiento getter de fecha de nacimiento
 * @method getGenero getter del genero
 */
export class Persona 
{
    private nombre: string;
    private apellidos: string;
    private fechaNacimiento: string;
    private genero: string;

    constructor(nombre: string, apellidos: string, fechaNacimiento: string,
                genero: string) {
                    this.nombre = nombre;
                    this.apellidos = apellidos;
                    this.fechaNacimiento = fechaNacimiento;
                    this.genero = genero;
                }

    public getNombre ()
    {
        return this.nombre;
    }

    public getApellidos ()
    {
        return this.apellidos;
    }

    public getFechaNacimiento ()
    {
        return this.fechaNacimiento;
    }

    public getGenero ()
    {
        return this.genero;
    }

    public setNombre(nombreNuevo: string)
    {
        this.nombre = nombreNuevo;
    }

    public setApellidos(apellidosNuevo: string)
    {
        this.apellidos = apellidosNuevo;
    }

    public setFecha(fechaNueva: string)
    {
        this.fechaNacimiento = fechaNueva;
    }

    public setGenero(generoNuevo: string)
    {
        this.genero = generoNuevo;
    }

}
